var nav_data = originData.data

var ul = document.createElement("ul")
document.querySelector("#middle").appendChild(ul)
for (var idx in nav_data) {
    var phone = nav_data[idx]

    var li = document.createElement("li")
    li.innerText = phone.name
    ul.appendChild(li)

    li.index = idx



    li.onmouseenter = function(e) {

        document.querySelector(".middle-content").innerHTML = ""
        document.querySelector(".middle-content").style.opacity = "1"
        document.querySelector(".middle-content").style.height = "230px"
        var data = nav_data[e.target.index]
        var content = data.content

        var divs = document.createElement("div")
        document.querySelector(".middle-content").appendChild(divs)

        for (var i in content) {

            var obj = content[i]

            // var img = document.createElement("img")
            // img.src = obj.imgSrc
            // li.appendChild(img)



            var section = document.createElement("section")
            section.className = "header-content"
            divs.appendChild(section)

            var img = document.createElement("img")
            img.src = obj.imgSrc
            img.style.width = "150px"
            section.appendChild(img)

            var p1 = document.createElement("p")
            p1.innerText = obj.type
            p1.style.margin = 0
            section.appendChild(p1)

            var p2 = document.createElement("p")
            p2.style.margin = 0
            p2.innerText = obj.price
            section.appendChild(p2)
        }
    }
    li.onmouseleave = function() {
        document.querySelector(".middle-content").style.opacity = "0"
        document.querySelector(".middle-content").style.height = "0px"
    }
    document.querySelector(".middle-content").onmouseenter = function(){
        document.querySelector(".middle-content").style.opacity = "1"
        document.querySelector(".middle-content").style.height = "230px"
    }
    document.querySelector(".middle-content").onmouseleave = function(){
        document.querySelector(".middle-content").style.opacity = "0"
        document.querySelector(".middle-content").style.height = "0px"
    }
}